hudson.tasks.Shell.command:
echo "Getting deployer-pom from ${DeilveryVerision} repository." if [ "${DeilveryVerision}" == "TRUNK" ]; then wget -O pom.xml https://versjonskontroll.adeo.no/wsvn/nav/trunk/int/esb/configuration/datapower-deployer/${Gateway}-deployer/pom.xml?op=dl\&rev=${DeployerPomRevision} elif [ "${DeilveryVerision}" == "2013-HL4" ]; then wget -O pom.xml https://versjonskontroll.adeo.no/wsvn/nav/branches/int/R_7_9_0_D20131113_2013HL4/configuration/datapower-deployer/${Gateway}-deployer/pom.xml?op=dl\&rev=${DeployerPomRevision} elif [ "${DeilveryVerision}" == "2014-HL1" ]; then wget -O pom.xml https://versjonskontroll.adeo.no/svn/nav/branches/datapower/D20140120_2014HL1_DATAPOWER/datapower-deployer/${Gateway}-deployer/pom.xml? fi
EnvironmentClass=${Environment:0:1}
#Check if deploy is done for sikkerhetslytter. if [ $Gateway = "sikkerhetslytter" ] then #Set node to the instance number if [[ $DataPowerInstance =~ "-12." ]] then Node=2 else Node=1 fi fi
if [ "${Debug}" == "true" ] then mvn3 clean install -e -X -Dgateway-name=${Gateway} -Denvclass=${EnvironmentClass} -Denv=${Environment} -Denvironment-configuration-version=${EnvironmentConfigVersion} -Dnonenvironment-configuration-version=${NonenvironmentConfigVersion} -Ddatapower.baseurl=${DataPowerInstance} -Dnode=${Node}
else mvn3 clean install -e -Dgateway-name=${Gateway} -Denvclass=${EnvironmentClass} -Denv=${Environment} -Denvironment-configuration-version=${EnvironmentConfigVersion} -Dnonenvironment-configuration-version=${NonenvironmentConfigVersion} -Ddatapower.baseurl=${DataPowerInstance} -Dnode=${Node} fi
echo "Datapower bygget med:" echo " Gateway: ${Gateway}" echo " Kodebase: ${DeilveryVerision}" echo " Env: ${Environment}" echo " Env-class: ${EnvironmentClass}" echo " DP-env: ${EnvironmentConfigVersion}" echo " DP-non-env: ${NonenvironmentConfigVersion}" echo " DP-pom: ${DeployerPomRevision}" echo " Boks: ${DataPowerInstance}" echo " Node: ${Node}"
